def max_run(string):
    max_run = 0
    run_start = 0
    for i in range(0, len(string)):
        if string[i].isupper():
            if i != run_start:
                run_start = i
            max_run = max(max_run, i - run_start + 1)
        else:
            run_start = 0
    return max_run

